CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER VIEW `qte_actuel` AS select `produits`.`id` AS `id_produit`,`produits`.`idtype` AS `type`,if(isnull((select sum(`stock`.`qte`) from `stock` where ((`stock`.`etat` = 1) and (`stock`.`idproduit` = `produits`.`id`) and (`stock`.`mouvement` in ('E','S'))))),0,(select sum(`stock`.`qte`) from `stock` where ((`stock`.`etat` = 1) and (`stock`.`idproduit` = `produits`.`id`) and (`stock`.`mouvement` in ('E','S'))))) AS `qte_act` from `produits` group by `produits`.`id`;

