CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER VIEW `stock_actuel` AS select `e`.`libelle` AS `entrepot`,`p`.`id` AS `id_produit`,`p`.`reference` AS `reference`,`p`.`designation` AS `designation`,`p`.`stock_min` AS `seuil`,(select `q`.`qte_act` from `qte_actuel` `q` where (`p`.`id` = `q`.`id_produit`)) AS `qte_stock` from (`produits` `p` join `entrepots` `e`) where ((`e`.`id` = `p`.`id_entrepot`) and (`p`.`idtype` = 1)) group by `p`.`id`;

